script:
// Get the SVG elements
const progressCircle = document.getElementById('progressCircle');
const progressText = document.getElementById('progressText');

// Set the progress value (0-100)
const progressValue = 75; // Change this value as needed

// Calculate the dash offset based on the progress value
const circumference = 2 * Math.PI * parseFloat(progressCircle.getAttribute('r'));
const dashOffset = circumference * (1 - (progressValue / 100));

// Update the progress circle's dash offset
progressCircle.style.strokeDasharray = `${circumference},1000`;
progressCircle.style.strokeDashoffset = dashOffset;

// Update the progress text
progressText.textContent = `${progressValue}%`;
